mapper.select:
SELECT id, collect_name, create_on, `status`, sum(CASE WHEN `status` = 1 THEN 1 ELSE 0 END) AS cancellation, sum(CASE WHEN `status` = 0 THEN 1 ELSE 0 END) AS noCancellation, sum(*) all FROM collect_info WHERE delete_flag = 0
AND create_on = #{dto.createON}
AND status = #{dto.status}
AND collect_name like '%${dto.collectName}%'
ORDER BY create_on DESC

SELECT c.id, h.district_name AS district, h.hospital_name, c.collect_name, c.collect_sex, c.collect_nation, c.collect_birth_data, c.collect_card, p.package_name, c.tube_card, sampling_point, c.`status`, u.`name` as createName, c.create_on FROM collect_info c LEFT JOIN package_info p ON c.package_id=p.id LEFT JOIN hospital_info h ON h.id=c.hospital_id LEFT JOIN sys_user u ON c.create_by=u.user_id WHERE c.delete_flag = 0
and h.district_name = #{dto.district}
and c.hospital_id = #{dto.hospitalId}
and c.collect_name like '%${dto.collectName}%'
and c.collect_card like '%${dto.collectCard}%'
and c.`status` = #{dto.status}
and c.create_on BETWEEN #{dto.beginCreateON} and #{dto.endCreateON}
and c.hospital_id in
#{hospitalId}
ORDER BY c.create_on desc

SELECT h.district_name AS district, h.hospital_name, c.collect_name, c.collect_sex, c.collect_nation, c.collect_birth_data, c.collect_card, p.package_name, c.tube_card, sampling_point, c.`status`, u.`name` as createName, c.create_on FROM collect_info c LEFT JOIN package_info p ON c.package_id=p.id LEFT JOIN sys_user u ON c.create_by=u.user_id LEFT JOIN hospital_info h ON h.id=c.hospital_id WHERE c.delete_flag = 0
and h.district_name = #{dto.district}
and c.hospital_id = #{dto.hospitalId}
and c.collect_name like '${dto.collectName}'
and c.collect_card = #{dto.collectCard}
and c.`status` = #{dto.status}
and c.create_on BETWEEN #{dto.beginCreateON} and #{dto.endCreateON}
